glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
#if SHOW_OUT_LINE
uniform Outline {
  vec4 outlineColor;
  float outlineWidth;
};
float getBgAlpha() {
  vec4 color_up = texture(texture, v_uv0 + vec2(0, outlineWidth));
  vec4 color_down = texture(texture, v_uv0 - vec2(0, outlineWidth));
  vec4 color_left = texture(texture, v_uv0 - vec2(outlineWidth, 0));
  vec4 color_right = texture(texture, v_uv0 + vec2(outlineWidth, 0));
  float total = color_right.a + color_left.a + color_down.a + color_up.a;
  return clamp(total, 0.0, 1.0);
}
#endif
#if USE_TINT
uniform BlackTint {
  vec4 btint;
};
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  o *= texture(texture, v_uv0);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif
  o *= v_color;
  #if USE_TINT
  o += btint;
  #endif
  ALPHA_TEST(o);
  gl_FragColor = o;
  #if SHOW_OUT_LINE
    if (outlineWidth == 0.0) {
      return;
    }
    vec4 color_dest = outlineColor * getBgAlpha();
    vec4 color_src = o;
    gl_FragColor = color_src * color_src.a + color_dest * (1.0 - color_src.a);
  #endif
}

